'/*',
' * render - function to create placeholder div tag,and initialise the  component',
' */',
'FUNCTION render_jstree (',
'    p_region                IN apex_plugin.t_region,',
'    p_plugin                IN apex_plugin.t_plugin,',
'    p_is_printer_friendly   IN BOOLEAN',
') RETURN apex_plugin.t_region_render_result IS',
'',
'    c_region_static_id   CONSTANT VARCHAR2(255) := apex_escape.html_attribute(p_region.static_id);',
'    l_result             apex_plugin.t_region_render_result;',
'    l_init_select_item   VARCHAR2(255) := p_region.attribute_02;',
'    l_init_search_node   VARCHAR2(255) := p_region.attribute_04;',
'    l_contextmenu        VARCHAR2(255) := p_region.attribute_05;',
'    l_dnd                VARCHAR2(255) := p_region.attribute_06;',
'    l_search             VARCHAR2(255) := p_region.attribute_03;',
'BEGIN',
'    apex_plugin_util.debug_region(p_plugin,p_region);',
'  ',
'',
'  -- This is the jstree v1.0.0 file pre jquery 1.8 compatibility',
'    apex_javascript.add_library(',
'        p_name        => ''jstree.min'',',
'        p_directory   => p_plugin.file_prefix,',
'        p_version     => NULL',
'    );',
'',
'  -- Custom JS with tree initialisation',
'  -- TODO rename',
'',
'    apex_javascript.add_library(',
'        p_name        => ''tree_plugin_js'',',
'        p_directory   => p_plugin.file_prefix,',
'        p_version     => NULL',
'    );',
'',
'    sys.htp.p(''<div id="''',
'     || ''ct_''',
'     || c_region_static_id',
'     || ''"></div>'');',
'     ',
'  -- Javascript initialization',
'  -- TODO: create jquery wrapper and pass options in an options object instead of literals',
'    apex_javascript.add_onload_code(''init_trees({''',
'     || ''  "treeRegionId" : "''',
'     || sys.htf.escape_sc(''ct_'' || c_region_static_id)',
'     || ''"''',
'     || '',"ajaxIdent" : "''',
'     || apex_plugin.get_ajax_identifier',
'     || ''"''',
'     || '',"selectedItem" : "''',
'     || nvl(l_init_select_item,''null'')',
'     || ''"''',
'     || '',"search_node" : "''',
'     || nvl(l_init_search_node,''null'')',
'     || ''"''',
'     || '',"contextmenu" : "''',
'     || l_contextmenu',
'     || ''"''',
'     || '',"dnd" : "''',
'     || l_dnd',
'     || ''"''',
'     || '',"search" : "''',
'     || l_search',
'     || ''"''',
'     || '',"treeID" : "''',
'     || c_region_static_id',
'     || ''"''',
'     || ''});'');',
'',
'    RETURN l_result;',
'END render_jstree;',
'',
'/*',
' * ajax - function to process SQL query,and output JSON data for the chart',
' */',
'',
'FUNCTION ajaxjstree (',
'    p_region   IN apex_plugin.t_region,',
'    p_plugin   IN apex_plugin.t_plugin',
') RETURN apex_plugin.t_region_ajax_result IS',
'',
'    c              SYS_REFCURSOR;',
'    l_query        VARCHAR2(32767);',
'    l_return       apex_plugin.t_region_ajax_result;',
'   ',
'',
'--------- getjsoninit',
'',
'    FUNCTION getjsoninit RETURN VARCHAR2 IS',
'        l_sql_string   VARCHAR2(22000);',
'    BEGIN',
'        l_query := p_region.source;',
'        l_sql_string := ''SELECT node_id "id",name "text",icon "icon" ,'';',
'        l_sql_string := l_sql_string || ''CASE isleaf WHEN 0 THEN ''''true'''' ELSE ''''false'''' END "children",'';',
'        l_sql_string := l_sql_string || ''CASE isleaf  WHEN 0 THEN ''''closed'''' ELSE ''''leaf'''' END "state" '';',
'        l_sql_string := l_sql_string',
'         || ''FROM   ''',
'         || ''(''',
'         || l_query',
'         || '')''',
'         || '' WHERE  parent_id IS NULL '';',
'',
'        apex_debug.message(''getjsoninit1'' || l_sql_string);',
'        RETURN l_sql_string;',
'    END getjsoninit;',
'',
'--------- getjsoninit',
'',
'',
'--------- getjsonlazy',
'',
'    FUNCTION getjsonlazy ( node_id IN NUMBER ) RETURN VARCHAR2 IS',
'        l_sql_string   VARCHAR2(22000);',
'    BEGIN',
'        l_query := p_region.source;',
'        l_sql_string := ''SELECT node_id "id",name "text",icon "icon" ,'';',
'        l_sql_string := l_sql_string || ''CASE isleaf WHEN 0 THEN ''''true'''' ELSE ''''false'''' END "children",'';',
'        l_sql_string := l_sql_string || ''CASE isleaf  WHEN 0 THEN ''''closed'''' ELSE ''''leaf'''' END "state" '';',
'        l_sql_string := l_sql_string',
'         || ''FROM   ''',
'         || ''(''',
'         || l_query',
'         || '')''',
'         || '' WHERE  parent_id = ''',
'         || node_id;',
'',
'        apex_debug.message(''getjsoninit2'' || l_sql_string);',
'        RETURN l_sql_string;',
'    END getjsonlazy;',
'',
'--------- getjsonlazy',
'',
'BEGIN',
'    l_query := p_region.source;',
'--htp.p(''getjsoninit3 ''||getjsoninit());',
'    IF',
'        apex_application.g_x02 = ''LOAD'' AND apex_application.g_x01 = 0',
'    THEN',
'        OPEN c FOR getjsoninit();',
'     --htp.p(''getjsoninit4 ''||getjsoninit());',
'',
'    ELSIF apex_application.g_x02 = ''LOAD'' AND apex_application.g_x01 != 0 THEN',
'        OPEN c FOR getjsonlazy(apex_application.g_x01);',
'',
'    END IF;',
'',
'    apex_json.write(c);',
'    RETURN l_return;',
'END ajaxjstree;'))
